    }
    @include customMedia("tablet") {
        position: relative;
        flex-direction: column;
        align-items: flex-start;
        justify-content: flex-start;
        box-sizing: border-box;
        padding-top: 0.75rem;
        height: 3rem;
        transition: all 0.3s;
        &.active {
            height: 430px;
        }
        .nav__home {
            .nav__logo {
                display: none;
            }
            .nav__mobile-logo {
                display: block;
                aspect-ratio: 1 / 1;
                height: 24px;
            }
        }
        .nav__links {
            flex-direction: column;
            width: 100%;
            .nav__menu-links,
            .nav__auth-links {
                padding: 1rem 0;
                align-items: flex-start;
                flex-direction: column;
                width: 100%;
                .nav__separator {
                    display: none;
                }
                .nav__menu-links__item,
                .nav__auth-links__item {
                    padding: 0.25rem 0;
                    font-size: $font-s;
                }
            }
            .nav__auth-links {
                border-top: 1px solid $separator-color;
            }
        }
        .nav__btn {
            display: block;
            position: absolute;
            right: 1rem;
            top: 0.75rem;
            .fa-bars {
                font-size: 1.5rem;
                color: white;
            }
        }
    }
}

.header {
    background: black;
    padding: 3.5rem 1rem 2.5rem 1rem;
    .header__container {
        display: flex;
        @include _setMarginAuto();
    }
    .header__copies {
        width: 600px;
        color: white;
        .header__main-copy,
        .header__sub-copy,
        .header__platform-info {
            margin: 0;
        }
        .header__platform-info {
            color: $blue-color;
            font-size: $font-s;
            font-weight: 400;
            padding-bottom: 1rem;
        }
        .header__main-copy {
            font-weight: 500;
            font-size: 2.5rem;
            padding-bottom: 2.5rem;
            line-height: 1.4;
        }
        .header__sub-copy {
            font-size: $font-xl;
            font-weight: 400;
            padding-bottom: 2rem;
            line-height: 1.65;
            color: $sub-white-color;
        }
        .header__main-copy > span,
        .header__sub-copy > span {
            display: block;
        }
    }
    .header__image {
        width: auto;
        max-height: 350px;
        max-width: 540px;
    }
    .header__sign-up-btn {
        @include _button($isBanner: false);
    }
    // ~991px;
    @include customMedia("tablet") {
        .header__container {
            flex-direction: column;
        }
        .header__copies {
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 100%;
            text-align: center;
            .header__main-copy > span {
                display: inline;
            }
            .header__sign-up-btn {
                margin-bottom: 2rem;
            }
        }
        .header__image-box {
            display: flex;
            justify-content: center;
            width: 100%;
        }
        .header__image {
            width: 100%;
        }
    }
    // 767px;
    @include customMedia("mobile-and-tablet") {
        padding : 2.5rem 1rem 2.5rem 1rem; 
        .header__copies {
            .header__main-copy > span {
                font-size: 2.375rem;
                display: block;
            }
            .header__sub-copy > span {
                font-size: $font-l;
            }
        }
    }
    // 575px;
    @include customMedia("mobile") {
        .header__copies {
            box-sizing: border-box;
            .header__main-copy {
                padding-bottom: 2rem;
                span {
                    font-size: 2.125rem;
                }
            }
        }        
    }
}

.programs {
    padding: 5rem 0 3.5rem 0; // 윤곽 패딩해주고!
    .programs__container {
        position: relative;
        @include _setMarginAuto();
        box-sizing: border-box;
        .programs__header {
            display: flex;
            justify-content: space-between;
            width: 100%;
            padding-bottom: 1.5rem;
            .programs__head {
                display: flex;
                flex-direction: row;
                align-items: center;
                .programs__title-and-info {
                    display: flex;
                }
                .programs__title {
                    font-size: $font-xl;
                    margin: 0;
                }
                .programs__info-btn {
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    font-size: $font-l;
                }
                .programs__more-btn {
                    @include _setMoreBtn();
                }
            }
            .programs__move-btn {
                display: flex;
                .programs__move-right {
                    margin-left: 0.5rem;
                }
                .programs__move-left,
                .programs__move-right {
                    @include _button(true);
                    background: transparent;
                    border: 1px solid;
                    border-color: $border-gray-color;
                    color: $border-gray-color;
                    width: 3.5rem;
                    height: 2.25rem;
                    .fa-chevron-left,
                    .fa-chevron-right {
                        color: $program-gray-color;
                    }
                }
                .programs__move-left--disable,
                .programs__move-right--disable {
                    opacity: 0.5;
                    cursor: auto;
                }
            }
        }
        .programs__card-box {
            position: static;
            width: 100%;
            overflow: hidden;
            .programs__label {
                position: absolute;
                left: -2.75rem;
                top: 0.75rem;
                z-index: 10;
                width: 4.5rem;
                height: 0;
                border-right: 2.5rem solid transparent;
                border-bottom: 2.5rem solid #142029;
                border-left: 2.5rem solid transparent;
                transform: rotate(-45deg);
                color: white;
                text-align: center;
                line-height: 2.5;
                &::before,
                &::after {
                    position: absolute;
                    display: block;
                    width: 0;
                    height: 0;
                    content: "";
                }
                &::before {
                    border-left: 0.625rem solid gray;
                    border-bottom: 0.625rem solid transparent;
                    left: -2.5rem;
                    top: 2.5rem;
                }
                &::after {
                    border-right: 0.625rem solid gray;
                    border-bottom: 0.625rem solid transparent;
                    right: -2.5rem;
                    bottom: -3.125rem;  
                }
            }
        }
        .programs__program-cards {
            display: inline-flex;
            position: relative;
            transition: all 0.3s;

            // 라벨이 숨겨지지 않도록 패딩 지정!
            padding: 0.5rem 0 0 0.5rem;

            /*
                ! JSON으로 받아오는 데이터
            */ 
            .programs__program-card {
                display: flex;
                position: relative;
                margin-right: 1rem;
                .programs__card-items {
                    //여기서 너비가 수정되어야 이미지가 중앙 정렬이 된다!
                    overflow: hidden;
                    border: 1px solid $border-gray-color;
                    border-radius: 10px;
                    // width: 1160px;
                    box-sizing: border-box;
                }         
                .programs__link {
                    display: block;
                    position: relative;
                    height: 17.5rem;
                    overflow: hidden;
                    box-sizing: border-box;
                    .programs__image {
                        position: absolute;
                        height: 100%;
                        left: 50%;
                        top: 50%;
                        border-radius: 10px 10px 0 0;
                        transform: translate(-50%, -50%);
                    }
                }
                .programs__program-info {
                    display: block;
                    position: relative;
                    padding: 1rem;
                    .programs__program-times {
                        display: flex;
                        flex-direction: column;
                        font-weight: 400;
                        .programs__period-receipt,
                        .programs__period-test {
                            color: $program-gray-color;
                            font-size: $font-s;
                            line-height: 1.75;
                        }
                        .programs__period-test {
                            margin-top: 0.25rem;
                        }
                    }
                    .programs__program-language {
                        display: flex;
                        .programs__language-item {
                            background: rgb(46, 46, 46);
                            margin-right: 1rem;
                            color: white;
                            padding: 0.25rem 1rem;
                            border-radius: 1rem;
                        }
                        .programs__more-language {
                            font-size: $font-xl;
                        }
                    }
                }
            }
        }
    }
    .programs__page-btns {
        display: flex;
        justify-content: center;
        padding-top: 2rem;
        li {
            margin: 0 0.25rem;
            width: 0.625rem;
            height: 0.625rem;
            border-radius: 0.75rem;
            cursor: pointer;
            background: $border-gray-color;
            transition: all 0.3s;
            &.programs__page-btn--active {
                background: $blue-color;
                width: 3rem;
            }
        }
    }

    @include customMedia("tablet") {
        padding-top: 2.5rem;
        .programs__container {
            padding: 0 2.75rem;
            .programs__card-box {
                .programs__program-cards {
                    .programs__program-card {
                        margin-right: 2rem;
                        .programs__card-items {
                            .programs__link {
                                height: 12.5rem;
                            }
                        }
                    }
                }
            }
        }
    }
    @include customMedia("mobile-and-tablet") {
        .programs__container {
            padding: 0 1rem;
        }
    }
    @include customMedia("mobile") {
        .programs__container {
            .programs__header {
                .programs__head {
                    display: flex;
                    width: 100%;
                    justify-content: space-between;
                }
                .programs__move-btn {
                    display: none;
                }
            }
        }
    }
}

.job {
    @include _setMarginAuto();
    &__header {
        display: flex;
        padding-bottom: 1rem;
        .job__title {
            font-size: 1.25rem;
            margin: 0;
        }
        .job__more-btn {
            @include _setMoreBtn();
        }
    }
    &__skills {
        display: flex;
        flex-wrap: wrap;
        padding-bottom: 2.5rem;
        .job__skill{
            background: #eeebff;
            padding: 0.125rem 1rem;
            margin: 0.25rem 0.5rem 0.25rem 0;
            color: $blue-color;
            font: {
                weight: 500;
                size: 0.875rem;
            }
            line-height: 2;
            border-radius: 0.25rem;
        }
    }
    &__cards {
        display: flex;
        flex-wrap:wrap;
        justify-content: space-between;
    }
    &__card {
        display: flex;
        width: calc(50% - 0.5rem);
        padding: 1rem;
        border: 1px solid lighten($job-gray-color, 20%);
        border-radius: 5px;
        margin-bottom: 1rem;
        box-sizing: border-box;
        .job__logo-box {
            width: 5rem;
            height: 5rem;
            overflow: hidden;
            .job__card-logo {
                width: 100%;
                max-width: 5rem;
                max-height: 5rem;
                // margin: 0 auto;
                vertical-align: middle;
            }
        }
        .job__card-info {
            // width를 계산하지 않을 시 왼쪽 이미지 박스를 침범함.
            width: calc(100% - 5rem);
            padding-left: 0.5rem;
            box-sizing: border-box;
            .job__card-title {
                margin: 0;
            }
            .job__card-brand {
                margin: 0;
                padding: 0.25rem 0;
                color: $job-gray-color;
                font-weight: 400;
                font-size: $font-s;
            }
            .job__requirements {
                position: relative;
                max-height: 2rem;
                padding-bottom: 0.5rem;
                overflow: hidden;
                box-sizing: border-box;
                .job__requirement {
                    display: inline-block;
                    margin: 0 0.25rem 1rem 0;
                    padding: 0.1875rem 0.5rem;
                    border-radius: 0.25rem;
                    background: lighten($job-gray-color, 25%);
                    font-size: $font-ms;
                    line-height: 1.5;
                }
                &::after {
                    // display: block;
                    position: absolute;
                    background: linear-gradient(0.25turn, rgba(255, 255, 255, 0), rgba(255, 255, 255, 255) 85%);
                    top: 0;
                    left: 80%;
                    right: 0;
                    bottom: 0;
                    z-index: 11;
                    content: "";
                }
            }
        }
    }
}

