            position: static;
            width: 100%;
            padding-right: 0.5rem;
            overflow: hidden;
            .programs__label {
                position: absolute;
                left: -2.75rem;
                top: 0.75rem;
                z-index: 10;
                width: 4.5rem;
                height: 0;
                border-right: 2.5rem solid transparent;
                border-bottom: 2.5rem solid #142029;
                border-left: 2.5rem solid transparent;
                transform: rotate(-45deg);
                color: white;
                text-align: center;
                line-height: 2.5;
                &::before,
                &::after {
                    position: absolute;
                    display: block;
                    width: 0;
                    height: 0;
                    content: "";
                }
                &::before {
                    border-left: 0.625rem solid gray;
                    border-bottom: 0.625rem solid transparent;
                    left: -2.5rem;
                    top: 2.5rem;
                }
                &::after {
                    border-right: 0.625rem solid gray;
                    border-bottom: 0.625rem solid transparent;
                    right: -2.5rem;
                    bottom: -3.125rem;  
                }
            }
        }
        .programs__program-cards {
            display: flex;
            position: relative;
            transition: all 0.3s;

            // 라벨이 숨겨지지 않도록 패딩 지정!
            padding: 0.5rem 0 0 0.5rem;

            /*
                ! JSON으로 받아오는 데이터
            */ 
            .programs__program-card {
                display: flex;
                position: relative;
                margin-right: 1rem;
                .programs__card-items {
                    //여기서 너비가 수정되어야 이미지가 중앙 정렬이 된다!
                    overflow: hidden;
                    border: 1px solid $border-gray-color;
                    border-radius: 10px;
                    width: 1160px;
                }         
                .programs__link {
                    display: block;
                    position: relative;
                    height: 17.5rem;
                    overflow: hidden;
                    box-sizing: border-box;
                    .programs__image {
                        position: absolute;
                        height: 100%;
                        left: 50%;
                        top: 50%;
                        border-radius: 10px 10px 0 0;
                        transform: translate(-50%, -50%);
                    }
                }
                .programs__program-info {
                    display: block;
                    position: relative;
                    padding: 1rem;
                    .programs__program-times {
                        display: flex;
                        flex-direction: column;
                        font-weight: 400;
                        .programs__period-receipt,
                        .programs__period-test {
                            color: $program-gray-color;
                            font-size: $font-s;
                            line-height: 1.75;
                        }
                    }
                    .programs__program-language {
                        display: flex;
                        padding-top: 1rem;
                        li {
                            background: rgb(46, 46, 46);
                            margin-right: 1rem;
                            color: white;
                            padding: 0.25rem 1rem;
                            border-radius: 1rem;
                        }
                    }
                }
            }
        }
    }
    .programs__page-btns {
        display: flex;
        justify-content: center;
        padding-top: 2rem;
        li {
            margin: 0 0.25rem;
            width: 0.625rem;
            height: 0.625rem;
            border-radius: 0.75rem;
            cursor: pointer;
            background: $border-gray-color;
            transition: all 0.3s;
            &.programs__page-btn--active {
                background: $blue-color;
                width: 3rem;
            }
        }
    }
}

